    { name: 'Räume', value: stats.raeume, color: '#ef4444' },
    { name: 'Kurse', value: stats.kurse, color: '#6366f1' },
    { name: 'Anmeldungen', value: stats.anmeldungen, color: '#8b5cf6' },
  ];

